// 9x解密 & 笔趣阁解密
getResult = (host, path, run) => {
    url = String(host + path);
    if (url.match("#dns#")) {
        url = url.replace(
            /#dns#([\s\S]+)/,
            ($0, $1) => $1 + ',{"headers":{"Host":"gfnormal05at.com"}}'
        );
    }
    // java.log(url);
    try {
        Res = java.ajaxAll([url])[0];
        if (Res.code() == 200) {
            result = String(Res.body());
            try {
                JSON.parse(result);
            } catch (parse_err) {
                subStr = result.substring(3, result.length - 4);
                result = java.createSymmetricCrypto("DES/CBC/PKCS5Padding", "6CB1E21E", "1F0FB845").decryptStr(subStr);
            }
            cipher = java.createSymmetricCrypto("DESede/CBC/PKCS5Padding", "OW84U8Eerdb99rtsTXWSILDO", "SK8bncVu");
            result = String(result)
                .replace(/[^"]*[{}]{6,}([^"]+)/g, ($0, $1) => String(cipher.decryptStr($1))
                    .replace(/\n/g, '\\n')
                    .replace(/\r/g, '\\r')
                    .replace(/\f/g, '\\f')
                    .replace(/\t/g, '\\t')
                    .replace(/\"/g, '\\"')
                ).replace(/(\}|\])\s*,\s*(\}|\])/g, '}$1$2')
                .replace(/src="(.+?)"/g, 'src=\\"$1\\"');
            $ = JSON.parse(result).result;
            if (run) {
                arr2.length = arr2.length - 1;
                arr3 = arr3.concat($.hosts);
                return true;
            } else {
                return result;
            }
        } else {
            arr1.length = arr1.length - 1;
            return false;
        }
    } catch (err) {
        arr1.length = arr1.length - 1;
        return false;
    }
}
// 更新接口
verifyUrls = () => {
    try {
        for (host of _.url) {
            arr1.push(host);
            arr2.push(host);
            getResult(host, _path.GetHost, 1);
        }
        // 去重
        _.url = arr1.concat(arr3)
            .filter((value, index, self) => {
                return self.indexOf(value) == index && arr2.indexOf(value) == -1;
            });
        _.time = time
        setv(_);
        java.toast("接口校验成功");
    } catch (err) {
        java.toast("未知错误：" + err);
    }
}
// 加载 / 校验
if (_.time < time) {
    verifyUrls();
}
if (_.url[0]) {
    try {
        getResult(_.url[0], uri);
    } catch (err) {
        verifyUrls();
        getResult(_.url[0], uri);
    }
} else {
    java.toast("没有接口");
}

