function encrypt(bytes) {
    var a = "zc89s30ipHG2Dw",

    key = a.padEnd(32, "\0"),
    iv = a.padEnd(16, "\0"),

    b = this.java.createSymmetricCrypto(
            "aes/cbc/pkcs7padding",
            key,
            iv
        )
        .encryptBase64(bytes)

    return encodeURIComponent(b)
}

