    keyBytes,
    { iv: ivBytes, mode: CryptoJS.mode.CBC, padding: CryptoJS.pad.ZeroPadding }
  );
  const decryptedText = decrypted.toString(CryptoJS.enc.Utf8);
 return decryptedText;
  
}
