loginUrl:
@js:
function login() {
  let une = source.getLoginInfoMap().get("账号")
  let pwd = source.getLoginInfoMap().get("密码")
  if (une && pwd) {
    let body = String('LoginForm%5Busername%5D=' + une + '&LoginForm%5Bpassword%5D=' + pwd + '&_17mb_sava=on&ac=login')
    let url = source.bookSourceUrl + 'login.php'
    let ck = java.post(url, body, { "Content-Type": "application/x-www-form-urlencoded" }).cookies()
    let header = JSON.stringify({
      "Cookie": String(ck).match(/\{(.*?)\}/)[1].replace(/,/g, ';')
    })
    source.putLoginHeader(header)
  }
}

