function generateUserAgent(deviceOption = '') {
    // ç”¨äºéšæœºç”ŸæˆUA
    // è¾“å…¥çš„ç¬¬ä¸€ä¸ªå‚æ•°å¤§å°ä¸æ•æ„Ÿ

    // å®šä¹‰å¸¸è§çš„æµè§ˆå™¨ã€æ“ä½œç³»ç»Ÿå’Œè®¾å¤‡ç±»å‹
    var browsers = [
