    ];
    var osMobile = [
        "Android 11; Mobile", // é«˜å…¼å®¹çš„å®‰å“ç‰ˆæœ¬
        "iPhone; CPU iPhone OS 13_1 like Mac OS X" // é«˜å…¼å®¹çš„ iOS ç‰ˆæœ¬
    ];
    var random = function(arr) {
        return arr[Math.floor(Math.random() * arr.length)];
    };

    // æ ¹æ®ç¬¬ä¸€ä¸ªå‚æ•°ç”Ÿæˆæ“ä½œç³»ç»Ÿå’Œè®¾å¤‡ç±»å‹
    var os, device;
    switch ((deviceOption || '').toLowerCase()) {
        case 'ç”µè„‘':
            os = random(osPC); // éšæœºé€‰æ‹© PC æ“ä½œç³»ç»Ÿ
            device = ''; // PC ç‰ˆä¸éœ€è¦è®¾å¤‡æ ‡è¯†
            break;
        case 'æ‰‹æœº': // æ‰‹æœºç‰ˆ
            os = random(osMobile); // éšæœºé€‰æ‹©é«˜å…¼å®¹çš„æ‰‹æœºæ“ä½œç³»ç»Ÿ
            device = 'Mobile'; // æ·»åŠ æ‰‹æœºè®¾å¤‡æ ‡è¯†
            break;
        default: // é»˜è®¤éšæœºé€‰æ‹©
            var allOS = osPC.concat(osMobile); // æ‰€æœ‰æ“ä½œç³»ç»Ÿ
            os = random(allOS); // éšæœºé€‰æ‹©æ“ä½œç³»ç»Ÿ
            device = os.indexOf('Android') !== -1 || os.indexOf('iPhone') !== -1 ? 'Mobile' : ''; // å¦‚æœæ˜¯æ‰‹æœºæ“ä½œç³»ç»Ÿï¼Œæ·»åŠ è®¾å¤‡æ ‡è¯†
    }

    // å›ºå®šä¸ºé«˜å…¼å®¹ç‰ˆæœ¬
    var browser = random(browsers);
