let replaceCover = (u) => {
  if (u.startsWith("https://")) u = u.substring(8)
  else u = u.substring(7)
  let uArr = u.split("/")
  uArr[0] = "https://p6-novel.byteimg.com/origin"
  let uArr2 = []
  uArr.forEach((x) => {
    if (!x.includes("?") && !x.includes("~")) uArr2.push(x)
    else uArr2.push(x.split("~")[0])
  })
  u = uArr2.join("/")
  return u
}

function getNovelComment(book_id, type) {
    const { java, source, book } = this
    if (type == 0) {
    let count = book.getVariable("custom")
    if (count && count == "") count = 20
    let res = java.ajax(`https://changdunovel.com/reading/ugc/novel_comment/book/v1?query_type=0&offset=0&count=${count}&sort=create_time&need_hot_comment=0&book_id=${book_id}&aid=1967&addQueryPrefix=true`) 
    let comments = JSON.parse(res).data.comment
    let result = `${comments.length>0?("(共 "+ comments.length +" 条)"):""}\n`
    comments.forEach(c => {
        let startNum = 5 - parseInt(c.score/2)
        result += `${c.user_info.gender==1?"👨🏻":"👱🏻"} ${c.user_info.user_name} || ${"★★★★★★☆☆☆☆☆".slice(startNum, startNum + 5)}\n${c.text.replace(/###\s/g,'')}\n&lrm;\n`
    })
    return result
    } 
  result = ""
  return result
}

