function se(n){return Number(list[n].attr("href").match(/\d+(?=\.)/))}
function re(n){return String(list[n].attr("href")).replace(/\d+(?=\.)/,se(n)+num)}
chapter = []
list = result.toArray()
num = se(1) - se(0)
for (i in list){
text = list[i].text()
href = list[i].attr("href")
if(i%2!=0){if(se(i)-se(i-1)==0){href = re(i)}}
chapter.push({text:text,href:href})
}
