 	 result.author=result.author()
 	 return `《${gs('$.book.name')}》`
 },
 "author":()=>{
 	 result.kind=result.kind()
   return ge('$.book.contributors')
	  .map(v=>v.name)
	  .join('，')
	},
 "kind":()=>{
 	 result.wordcount=result.wordcount()	
 	 return "书单"
 },
 "wordcount":()=>{
   result.lastUpdate=result.lastUpdate()
   return `共${ge('$.book.docs').length}个分类`
 },
 "lastUpdate":()=>{
   result.intro=result.intro()
	  return `最后更新于${
	   java.timeFormat(new Date(gs('$.book.content_updated_at'))
	   .getTime())
	  }`
	},
 "intro":()=>gs('$.book.description'),
	"cover":'https://i.postimg.cc/sDjHTh9V/22-09-07-00-43-17.jpg',
 "url":baseUrl
}
result.Eval()


