@js:
function qixingge() {
    let disc = []
    let url = baseUrl.replace(/[^\w]+$/gi, '');
    let tagHtml = java.ajax(`${url}/top/`);
    let doc = org.jsoup.Jsoup.parse(tagHtml);
    let tags = doc.select(".content ul li a");
    Array.from(tags).forEach(a => {
        disc.push({
            title: a.ownText().trim(),
            url: String(a.attr("href")).replace(/_\d+/,"_{{page}}"),
            style: {
                layout_flexGrow: 1,
                layout_flexBasisPercent: 0.25
            }
        });
    });

    let tal = java.ajax(`${url}/sort/`);
    let dc = org.jsoup.Jsoup.parse(tal);
    let tas = dc.select(".content ul li a");
    Array.from(tas).forEach(a => {
        disc.push({
            title: a.ownText().trim(),
            url: String(a.attr("href")).replace(/_\d+/,"_{{page}}"),
            style: {
                layout_flexGrow: 1,
                layout_flexBasisPercent: 0.25
            }
        });
    });

        return JSON.stringify(disc);
}
qixingge();

