	}
	let time = +java.timeFormatUTC(new Date(), 'yyyyMMdd', 28800000);
	try {
		if (!$.action || time - $.time > updateInterval) {
			java.log("è·å–action")
			let script = String(java.get(`http://www.shfljx.com/js/common.js`, source.getHeaderMap(false)).body())
			$.action = script.match(/action=[^"]+"([^?"]+)/)[1]
			$.time = time
			source.setVariable(JSON.stringify($))
		}
	} catch (e) {}
	return `${$.action}?ie=utf-8&q=${key}`
}

