ruleContent.content:
$.url@js:
let content=""
if(result){
let b64 = java.ajax(result)
let str=java.base64Decode(b64.match(/'(.+?)'/)[1])
let list=JSON.parse(str).p
for (i =0;i<list.length;i++){
content+= list[i]+"\n"
}
}else{
content+="购买后继续阅读本章内容"
}
content

ruleToc.nextTocUrl:
#pagelink.0@a@href
@js:
pages = java.getString(
	"em#pagestats.0@text", src);
yes = pages.match(/(\d+)$/)||[];
var page = yes[1], next =[];
if(page -1 > result.length){
	for(i = 2; i <= page; i++){
		url = baseUrl.replace(/\.html/,`/${i}`);
		next.push(url)
		}
	}else next = result;
next

