jsLib:
//随机UA
function generateUserAgent(deviceOption = '') {
    // 用于随机生成UA
    // 输入的第一个参数大小不敏感

    // 定义常见的浏览器、操作系统和设备类型
    var browsers = [
        { name: "Chrome", highCompat: "80.0.3987." },
        { name: "Firefox", highCompat: "78.0." },
        { name: "Safari", highCompat: "13.1." },
        { name: "Edge", highCompat: "87.0.664." }
    ];
    var osPC = [
        "Windows NT 10.0; Win64; x64",
        "Macintosh; Intel Mac OS X 13_5",
        "Linux x86_64"
    ];
    var osMobile = [
        "Android 11; Mobile", // 高兼容的安卓版本
        "iPhone; CPU iPhone OS 13_1 like Mac OS X" // 高兼容的 iOS 版本
    ];
    var random = function(arr) {
        return arr[Math.floor(Math.random() * arr.length)];
    };

    // 根据第一个参数生成操作系统和设备类型
    var os, device;
    switch ((deviceOption || '').toLowerCase()) {
        case '电脑':
            os = random(osPC); // 随机选择 PC 操作系统
            device = ''; // PC 版不需要设备标识
            break;
        case '手机': // 手机版
            os = random(osMobile); // 随机选择高兼容的手机操作系统
            device = 'Mobile'; // 添加手机设备标识
            break;
        default: // 默认随机选择
            var allOS = osPC.concat(osMobile); // 所有操作系统
            os = random(allOS); // 随机选择操作系统
            device = os.indexOf('Android') !== -1 || os.indexOf('iPhone') !== -1 ? 'Mobile' : ''; // 如果是手机操作系统，添加设备标识
    }

    // 固定为高兼容版本
    var browser = random(browsers);
    var version = browser.highCompat + Math.floor(Math.random() * 100); // 高兼容版本

    // 生成最终的 UA
    return "Mozilla/5.0 (" + os + ") AppleWebKit/537.36 (KHTML, like Gecko) " + browser.name + "/" + version + " Safari/537.36" + (device ? " " + device : "");
}

