ruleToc.chapterList:
+<js>
//二级目录，仅支持HTML
bookid=java.get('BookId');
var rules = {
  volsName: 
'//*[@id="directoryList"]//li[@d-name]/@d-name',
  volumes: '@css:#directoryList .item',
  chapters: '@css:.chapter',
  chName: 'd-title',
  chLink: 'd-id',
  vip: '@css:*[d-vip=1]'
};
var opts = {
  showVolName: false
};

function chapterLevel(rules, opts){
  //return Array
  //volsName: 卷标题规则（XPath）
  //volumes: 卷规则
  //volInfo(' '): 卷简介规则（volumes子规则）
  //chapters: 章节规则（volumes子规则）
  //chName: 章节标题规则（chapters子规则）
  //chLink: 章节url规则（chapters子规则）
  //vip(' '): 判定vip的规则（chapters子规则
  //showVolName(false): 是否在章节名称前显示卷名

  var check = (rule) => rule !== undefined && rule !== '' ? rule : '//x[@x="" and @x!=""]';
  rules.volInfo = check(rules.volInfo);
  rules.vip = check(rules.vip);
  opts.showVolName = opts.showVolName === undefined ? true : !!opts.showVolName;

  var NL = '<br/>', NP = "<br/>\uFEFF<br/>";
  var list = [];
  var volsName = java.getStringList(rules.volsName).toArray();

  java.getElements(rules.volumes)
    .toArray().forEach((vol,idx) => {
    java.setContent(vol);
    var chs = java.getElements(rules.chapters)
    .toArray().map(ch => {
      java.setContent(ch);
      var isVip = 0;
      var chName = java.getString(rules.chName,false);
      return {
        text:(isVip ? '💰' : '').padStart(4, "\u2000")+(opts.showVolName ? volsName[idx]+"\x20" : '')+chName,    href:'http://www.ebtang.com/book/readbook/'+bookid+'/'+java.getString(rules.chLink,false),
        name: chName
      };
    });

    var volInfo = '简介：' + NL + java.getStringList(rules.volInfo)
      .toArray().join(NL);
    volInfo += NP + '目录(' + chs.length + ')：' + NL;
    volInfo += chs.slice(0,10).map(v => v.name).join(NL);
    volInfo += chs.length > 10 ? NL + '……' : '';
    volInfo = volInfo.replace(/(\s)\1+/g, '$1');
    list.push({
      text: '🔻[ '+volsName[idx]+' ]',
      href: 'https://translate.google.cn/#view=home&op=translate&sl=zh-CN&tl=en&text='+encodeURIComponent(volInfo)
    });
    list = list.concat(chs);
  });

  return list;
}

chapterLevel(rules, opts);
</js>

