if (tag) {
    var a = org.jsoup.Jsoup.parse(tag).select(".nav li a");
    var subResult = [];
    for (var i in a) {
        var title = a[i].text();
        var url = a[i].attr("href");
        var size = 0.25;
        title = String(title).replace(/\s/g, "");
        url = String(url).replace(/\/$/, "") + "_{{page}}/";
        subResult.push({
            "title": title,
            "url": url,
            "style": {
                "layout_flexGrow": 1,
                "layout_flexBasisPercent": size
