@js:
function d(a, b) {
    b = java.md5Encode(b);
    var d = b.substring(0, 16);
    var e = b.substring(16);
    data = java.base64DecodeToByteArray(a);
    iv = java.strToBytes(d);
    key = java.strToBytes(e);
    return java
        .createSymmetricCrypto("AES/CBC/PKCS7Padding", key, iv)
        .decryptStr(data);
}
let decryptFunc = (a, b) => d(a, b);
let host = String(java.toURL(baseUrl).host);

switch (host) {
    case "www.ziyungong.com":
        content = eval(result.match(/(decryptFunc\("[\s\S]+?"\))/)[1]);
        break;
    default:
        content = eval(result.match(/(d\("[\s\S]+?"\))/)[1]);
}

