function decompressFromBase64(input) {
    if (input == null) return '';
    if (input === '') return null;

    const base64Alphabet = "ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789+/=";
    const lookup = {};
    for (let i = 0; i < base64Alphabet.length; i++) {
        lookup[base64Alphabet.charAt(i)] = i;
    }
    function getValue(char) {
        return lookup[char];
    }

    function decompress(length, resetValue, getNextValue) {
        const dictionary = [];
        let dictSize = 4;
        let bits = 3;
        let result = [];
        let data = { val: getNextValue(0), position: resetValue, index: 1 };

        // 读取初始控制代码（2 位）
        let res = 0;
        let maxPower = Math.pow(2, 2);
        let power = 1;
        while (power != maxPower) {
            let bit = data.val & data.position;
            data.position >>= 1;
            if (data.position == 0) {
                data.position = resetValue;
                if (data.index > length) return '';
                data.val = getNextValue(data.index++);
            }
            res |= (bit > 0 ? 1 : 0) * power;
            power <<= 1;
        }

        let next;
        switch (res) {
            case 0: // 8 位字符
                res = 0;
                maxPower = Math.pow(2, 8);
                power = 1;
                while (power != maxPower) {
                    let bit = data.val & data.position;
                    data.position >>= 1;
                    if (data.position == 0) {
                        data.position = resetValue;
                        if (data.index > length) return '';
                        data.val = getNextValue(data.index++);
                    }
                    res |= (bit > 0 ? 1 : 0) * power;
                    power <<= 1;
                }
                next = String.fromCharCode(res);
                break;
            case 1: // 16 位字符
                res = 0;
                maxPower = Math.pow(2, 16);
                power = 1;
                while (power != maxPower) {
                    let bit = data.val & data.position;
                    data.position >>= 1;
                    if (data.position == 0) {
                        data.position = resetValue;
                        if (data.index > length) return '';
                        data.val = getNextValue(data.index++);
                    }
                    res |= (bit > 0 ? 1 : 0) * power;
                    power <<= 1;
                }
                next = String.fromCharCode(res);
                break;
            case 2: // 结束
                return '';
        }

        // 初始化字典
        dictionary[0] = '';
        dictionary[1] = '';
        dictionary[2] = '';
        dictionary[3] = next;
        let w = next;
        result.push(next);

        // 主解压循环
        while (data.index <= length) {
            let code = 0;
            maxPower = Math.pow(2, bits);
            power = 1;
            while (power != maxPower) {
                let bit = data.val & data.position;
                data.position >>= 1;
                if (data.position == 0) {
                    data.position = resetValue;
                    if (data.index > length) break;
                    data.val = getNextValue(data.index++);
                }
                code |= (bit > 0 ? 1 : 0) * power;
                power <<= 1;
            }

            if (data.index > length && code === 0) break;

            switch (next = code) {
                case 0: // 新 8 位字符
                    res = 0;
                    maxPower = Math.pow(2, 8);
                    power = 1;
                    while (power != maxPower) {
                        let bit = data.val & data.position;
                        data.position >>= 1;
                        if (data.position == 0) {
                            data.position = resetValue;
                            if (data.index > length) return result.join('');
                            data.val = getNextValue(data.index++);
                        }
                        res |= (bit > 0 ? 1 : 0) * power;
                        power <<= 1;
                    }
                    dictionary[dictSize++] = String.fromCharCode(res);
                    next = dictSize - 1;
                    break;
                case 1: // 新 16 位字符
                    res = 0;
                    maxPower = Math.pow(2, 16);
                    power = 1;
                    while (power != maxPower) {
                        let bit = data.val & data.position;
                        data.position >>= 1;
                        if (data.position == 0) {
                            data.position = resetValue;
                            if (data.index > length) return result.join('');
                            data.val = getNextValue(data.index++);
                        }
                        res |= (bit > 0 ? 1 : 0) * power;
                        power <<= 1;
                    }
                    dictionary[dictSize++] = String.fromCharCode(res);
                    next = dictSize - 1;
                    break;
                case 2: // 结束
                    return result.join('');
            }

            if (dictSize - 1 >= Math.pow(2, bits) - 1) bits++;

            let entry;
            if (dictionary[next]) {
                entry = dictionary[next];
            } else if (next === dictSize) {
                entry = w + w.charAt(0);
            } else {
                return null;
            }

            result.push(entry);
            dictionary[dictSize++] = w + entry.charAt(0);
            w = entry;

            if (dictSize - 1 >= Math.pow(2, bits) - 1) bits++;
        }

        return result.join('');
    }

    return decompress(input.length, 32, (index) => getValue(input.charAt(index)));
}

